<template>
    <div class="container d-flex align-items-center justify-content-center">
        <div class="row col-8" dir="rtl">
            <div class="col-12 login-form">
                <form @submit.prevent="login">
                    <h2 class="text-center">ورود به سایت</h2>
                    <div class="alert alert-danger text-center" v-if="has_error">
                        متاسفانه اطلاعات ورودی به هم مطابقت ندارند :(    
                    </div>      
                    <div class="form-group">
                        <input type="email" v-model="form.email" class="form-control" placeholder="آدرس ایمیل" required="required" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; cursor: auto;">
                    </div>
                    <div class="form-group">
                        <input type="password" v-model="form.password" class="form-control" placeholder="رمز عبور" required="required" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; cursor: pointer;">
                    </div>
                    <div class="form-group">
                        <button type="submit" class="btn btn-primary btn-block">ورود</button>
                    </div>
                    <!-- <div class="clearfix">
                        <label class="pull-left checkbox-inline"><input type="checkbox"> Remember me</label>
                        <a href="#" class="pull-right">Forgot Password?</a>
                    </div>         -->
                </form>
                <!-- <p class="text-center"><a href="#">Create an Account</a></p> -->
            </div>
        </div>
    </div>
</template>

<script>
export default {
    data() {
        return {
            has_error: false,

            form: {
                email: 'admin@site.com',
                password: '123456'
            }
        }
    },
    mounted() {
        if ( localStorage.getItem('JWT') && localStorage.getItem('JWT') !== undefined )
            window.location.replace('/panel')
    },
    methods: {
        login() {
            axios({
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                },
                url: '/api/v1/login',
                data: this.form
            })
            .then( ({data}) => {
                this.has_error = false;
                localStorage.setItem('JWT', data.data.token)
                window.location.replace('/panel')
            })
            .catch( (error) => {
                this.has_error = true;
                console.log(error.response)
            })
        }
    }
}
</script>

<style>
html, body, .container {
    height: 100%;
}
</style>
